import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import api, { lastfmKey } from './services/api';
import ProgressBar from "@ramonak/react-progress-bar";

const ResultList = (props) => {
    return(
        <div
            className = 'list'
        >
            {props.list.map((item, index) => {
                return (
                    <div
                        className = {item.visible ? 'card' : 'hiddenCard'}
                        key = {index.toString()}
                    >
                        <span>{index + 1}</span>
                        {
                            (item.name.length < 20 && <h1>{item.name}</h1>)
                            || <h2>{item.name}</h2>
                        }

                        <p>{item.playcount} plays</p>
                    </div>
                )
            })}
        </div>

    )
}

const UsernameInput = (props) => {
    const [value, setValue] = useState('')

    return(
        <div
            className = {
                props.isFirstScreen ? 
                'usernameInputColumn' : 'usernameInputRow'
            }
        >
            <input
                className = 'usernameField' 
                placeholder ='dale'
                onChange = {e => setValue(e.target.value)}
                value = {value}
                disabled = {!props.isFirstScreen}
            /> 

            <input
                className = 'button' 
                type = 'submit'
                value = {props.isFirstScreen ? 'Buscar' : 'Mudar user'}
                onClick = {
                    props.isFirstScreen ? 
                    () => props.getList(value) : () => props.backToFirstScreen()
                }
            />
        </div>
    )
}

const TagInput = (props) => {
    const [value, setValue] = useState('')

    return(
        <div
            className = 'usernameInputRow'
        >
            <input 
                className = 'usernameField' 
                placeholder = 'Filtre por tag...'
                onChange = {e => setValue(e.target.value)}
                value = {value}
            />

            <input
                className = 'button' 
                type = 'submit'
                value = 'Filtrar'

                onClick = {() => props.filter(value)}
            />

        </div>
    )
}

