class Main extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            isFirstScreen: true,
            listToRender: [],
            username: null,
            error: false
        }
    }


    loadData = async (username) => {
        try{
            let result = await api.get(`?api_key=${lastfmKey}&method=user.gettopartists&username=${username}&limit=10&format=json`);
            let list = result.data.topartists.artist;

            this.setState({
                username,
                error: false,
                isFirstScreen: false,
                listToRender: list
            });

            this.renderList(list);

        }catch(e){
            this.setState({
                listToRender: [],
                error: true
            })
        }
    }

    filter = async (tag) => {
        this.setState({
            loadingProgress: 0
        })

        try{
            let page = 1;
            let newFilteredList = [];

            while(newFilteredList.length < 10){
                let listToFilter;
                if(page > 1){
                    let result = await api.get(
                        `?api_key=${lastfmKey}&method=user.gettopartists&username=${this.state.username}&limit=10&page=${page}&format=json`
                    );
                    listToFilter = result.data.topartists.artist;
                }else{
                    listToFilter = this.state.listToRender;
                }



                for(let item of listToFilter){
                    if(newFilteredList.length !== 10){
                        let result = await api.get(
                            `?api_key=${lastfmKey}&method=artist.gettoptags&artist=${encodeURIComponent(item.name)}&format=json`
                        );

                        let topTags = result.data.toptags.tag;
                        console.log(item.name)

                        let limit = topTags.length >= 5 ? 5 : topTags.length;

                        for(var i = 0; i < limit ; i++){
                            if(topTags[i].name === tag){
                                item.visible = false;
                                newFilteredList.push(item)
                            }
                        }
                    }
                }

                page++;
                this.setState({
                    loadingProgress: newFilteredList.length * 10
                })

            }

            this.setState({
                listToRender: newFilteredList,
            });

            this.renderList(newFilteredList);

        }catch(e){
            this.setState({
                listToRender: []
            })
        }
    }

    renderList = async (list) => {
        console.log(list)
        for(var item of list){
            await new Promise(resolve => setTimeout(resolve, 300))
            item.visible = true;
            this.setState({
                listToRender: list
            })      
        }
    }

    backToFirstScreen = () => {
        this.setState({
            listToRender: [],
            isFirstScreen: true,
            username: null,
            error: false
        })
    }

    render(){
        var { error, listToRender, isFirstScreen, loadingProgress } = this.state;

        return(
            <div className = {isFirstScreen ? 'main' : 'mainWithList'}>
                <UsernameInput
                    getList = {this.loadData}
                    isFirstScreen = {isFirstScreen}
                    backToFirstScreen = {this.backToFirstScreen}
                />
                {error && <p>Username n√£o encontrado</p>}

                {(!isFirstScreen &&
                    <div className="resultContent">
                        <TagInput
                            filter = {this.filter} 
                        />

                        {
                            (loadingProgress &&
                                <ProgressBar 
                                    completed = {loadingProgress}
                                />
                            ) || 
                            <ResultList
                                list = {listToRender}
                            />
                        }
                    </div>
                )}
            </div>
        ) 
    }
}

ReactDOM.render(<Main />, document.getElementById('root'))
