import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import api, { key } from './services/api';


const ResultList = (props) => {
    return(
        <ul>
            {props.list.map((item, index) => {
                return (
                    <li key = {index.toString()}> 
                        {item.name}
                    </li>
                )
            })}

        </ul>
    )
}

const UsernameInput = (props) => {
    const [value, setValue] = useState('')

    return(
        <div>
            <input 
                placeholder='dale'
                onChange = {e => setValue(e.target.value)}
                value = {value}
            />

            <input
                type = 'submit'
                value = 'Buscar'
                onClick = {() => props.getList(value)}
            />
        </div>
    )
}

const TagInput = (props) => {
    const [value, setValue] = useState('')

    return(
        <div>
            <input 
                placeholder='Filtre por tag...'
                onChange = {e => setValue(e.target.value)}
                value = {value}
            />

            <input
                type = 'submit'
                value = 'Filtrar'
                onClick = {() => props.filter(value)}
            />

        </div>
    )
}

/////////

const Main = () => {
    const [list, setList] = useState(null);
    const [error, setError] = useState(false);


    const loadData = async (username) => {
        try{
            let result = await api.get(`?api_key=${key}&method=user.gettopartists&username=${username}&limit=10&format=json`);

            let list = result.data.topartists.artist;

            setList(list);
            setError(false);
        }catch(e){
            setList(null);
            setError(true);
        }
    }

    const filter = async (tag) => {
        try{
            let filteredList = list.filter(async (item) => {
                let result = await api.get(
                    `?api_key=${key}&method=artist.gettoptags&artist=${item.name}&format=json`
                    );
                console.log(result);
                let tagList = result.data.toptags.tag;
                let matchingTag = false;

                for(let i; i < 4; i++){
                    if(tagList[i].name === tag){
                        matchingTag = true;
                    }
                }

                return matchingTag;
            })

            setList(filteredList)

        }catch{
            setList(null);
        }
    }

    return(
        <div>
            <UsernameInput
                getList = {loadData}
            />
            {error && <p>Username n√£o encontrado</p>}

            {(list !== null &&
                <div>
                    <TagInput
                        filter = {filter} 
                    />
                    <ResultList
                        list = {list}
                    />
                </div>
            )}
        </div>
    ) 

}

ReactDOM.render(<Main />, document.getElementById('root'))
