import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import './styles/styles.css';
import api, { lastfmKey, source } from './services/api';
import ReactLoading from 'react-loading';
import ProgressBar from "@ramonak/react-progress-bar";

const ResultList = (props) => {
    return(
        <div
            className = 'list'
        >
            {props.list.map((item, index) => {
                return (
                    <div
                        className = {item.visible ? 'card' : 'hiddenCard'}
                        key = {index.toString()}
                    >
                        <span>{index + 1}</span>
                        {
                            (item.name.length < 20 && <h1>{item.name}</h1>)
                            || <h2>{item.name}</h2>
                        }

                        <p>{item.playcount} plays</p>
                    </div>
                )
            })}
        </div>

    )
}

const UsernameInput = (props) => {
    const [value, setValue] = useState('')

    return(
        <div className = 'usernameInputColumn'>
            <input
                className = 'usernameField' 
                placeholder = 'Insira o nome de usuário'
                onChange = {e => setValue(e.target.value)}
                value = {value}
            /> 

            <input
                className = 'button' 
                type = 'submit'
                value = {'Buscar'}
                onClick = {() => props.getList(value)}
            />
        </div>
    )
}

const UsernameTitle = (props) => {
    return(
        <div className = 'usernameRow'>

            <p></p>

            <input
                className = 'button' 
                type = 'submit'
                value = {'Buscar'}
                onClick = {() => props.backToFirstScreen()}
            />
        </div>
    )
}

const TagInput = (props) => {
    const [value, setValue] = useState('')

    return(
        <div
            className = 'usernameInputRow'
        >
            <input 
                className = 'usernameField' 
                placeholder = 'Filtre por tag...'
                onChange = {e => setValue(e.target.value)}
                value = {value}
                disabled = {props.isLoadingInProgress}
            />

            <input
                className = 'button' 
                type = 'submit'
                value = {props.isLoadingInProgress ? 'Cancelar' : 'Filtrar'}

                onClick = {
                    props.isLoadingInProgress ? 
                    () => props.cancelFetching() : () => props.filter(value)
                }
            />

        </div>
    )
}

