import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import api, { key } from './services/api';


const ResultList = (props) => {
    return(
        <ul>
            {props.list.map(item => {
                return (
                    <li key = {item.mbid}> 
                        {item.name}
                    </li>
                )
            })}

        </ul>
    )
}

const UsernameInput = () => {
    const [value, setValue] = useState('')

    return(
        <div>
            <input 
                placeholder='dale'
                onChange = {e => setValue(e.target.value)}
                value = {value}
            />
            <input
                type = 'submit'
                placeholder = 'oi'
            />
        </div>
    )
}

/////////

const Main = () => {
    const [list, setList] = useState(null);

    useEffect(() => {
        async function loadData(){
            let top = await api.get(`?api_key=${key}&method=user.gettopartists&username=rheiselsilva&limit=10&format=json`)
            if(list == null){
                setList(top.data.topartists.artist);
                console.log(top)
            }
        }

        loadData();
    })

    return(
        <div>
            <UsernameInput />
            {(list !== null &&
                <ResultList
                    list = {list}
                />)
            }
        </div>
    ) 

}

ReactDOM.render(<Main />, document.getElementById('root'))
