import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import api, { key } from './services/api';


const ResultList = (props) => {
    return(
        <ul>
            {props.list.map(item => {
                return (
                    <li key = {item.mbid}> 
                        {item.name}
                    </li>
                )
            })}

        </ul>
    )
}

const UsernameInput = (props) => {
    const [value, setValue] = useState('')

    return(
        <div>
            <input 
                placeholder='dale'
                onChange = {e => setValue(e.target.value)}
                value = {value}
            />

            <input
                type = 'submit'
                value = 'Buscar'
                onClick = {() => props.getList(value)}
            />
        </div>
    )
}

/////////

const Main = () => {
    const [list, setList] = useState(null);


    const loadData = async (username) => {
        try{
            let top = await api.get(`?api_key=${key}&method=user.gettopartists&username=${username}&limit=10&format=json`)
            setList(top.data.topartists.artist);
            console.log(top)
        }catch(e){
            console.log(e)
        }
    }

    return(
        <div>
            <UsernameInput
                getList = {loadData}
            />
            {(list !== null &&
                <ResultList
                    list = {list}
                />)
            }
        </div>
    ) 

}

ReactDOM.render(<Main />, document.getElementById('root'))
