import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import api, { key } from './services/api';


const ResultList = (props) => {
    return(
        <ul>
            {props.data.chart.map((item, index) => {
                return (
                    <li key = {index.toString()}> 
                        {item.name}
                    </li>
                )
            })}

        </ul>
    )
}

const UsernameInput = (props) => {
    const [value, setValue] = useState('')

    return(
        <div>
            <input 
                placeholder='dale'
                onChange = {e => setValue(e.target.value)}
                value = {value}
            />

            <input
                type = 'submit'
                value = 'Buscar'
                onClick = {() => props.getList(value)}
            />
        </div>
    )
}

const TagInput = (props) => {
    const [value, setValue] = useState('')

    return(
        <div>
            <input 
                placeholder='Filtre por tag...'
                onChange = {e => setValue(e.target.value)}
                value = {value}
            />

            <input
                type = 'submit'
                value = 'Filtrar'
            />

        </div>
    )
}

/////////

const Main = () => {
    const [data, setData] = useState(null);
    const [error, setError] = useState(false);


    const loadData = async (username) => {
        try{
            let top = await api.get(`?api_key=${key}&method=user.gettopartists&username=${username}&limit=10&format=json`)

            let data = {
                chart: top.data.topartists.artist
            }

            setData(data)
            setError(false)
        }catch(e){
            setData(null)
            setError(true)
        }
    }

    return(
        <div>
            <UsernameInput
                getList = {loadData}
            />
            {error && <p>Username n√£o encontrado</p>}

            {(data !== null &&
                <div>
                    <TagInput />
                    <ResultList
                        data = {data}
                    />
                </div>
            )}
        </div>
    ) 

}

ReactDOM.render(<Main />, document.getElementById('root'))
