import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import api, { key } from './services/api';


const ResultList = (props) => {
    return(
        <div>
            {props.list.map((item, index) => {
                return (
                    <div
                        className = 'card'
                        key = {index.toString()}
                    >
                        <img
                            src = 'https://iela.ufsc.br/sites/default/files/default_images/default-user.png' 
                        />
                        <h3>{item.name}</h3>
                        <p>{item.playcount}</p>
                    </div>

                )
            })}
        </div>

    )
}

const UsernameInput = (props) => {
    const [value, setValue] = useState('')

    return(
        <div
            className = {
                props.displayOnCenter ? 
                'usernameInputColumn' : 'usernameInputRow'
            }
        >
            <input
                className = 'usernameField' 
                placeholder ='dale'
                onChange = {e => setValue(e.target.value)}
                value = {value}
            /> 

            <input
                className = 'button' 
                type = 'submit'
                value = 'Buscar'
                onClick = {() => props.getList(value)}
            />
        </div>
    )
}

const TagInput = (props) => {
    const [value, setValue] = useState('')

    return(
        <div
            className = 'usernameInputRow'
        >
            <input 
                className = 'usernameField' 
                placeholder = 'Filtre por tag...'
                onChange = {e => setValue(e.target.value)}
                value = {value}
            />

            <input
                className = 'button' 
                type = 'submit'
                value = 'Filtrar'

                onClick = {() => props.filter(value)}
            />

        </div>
    )
}

/////////

class Main extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            list: null,
            filteredList: [],
            username: null,
            error: false
        }
    }


    loadData = async (username) => {
        try{
            let result = await api.get(`?api_key=${key}&method=user.gettopartists&username=${username}&limit=10&format=json`);
            let list = result.data.topartists.artist;
            console.log(list)
            this.setState({
                list,
                username,
                error: false
            })
        }catch(e){
            this.setState({
                list: null,
                error: true
            })
        }
    }

    filter = async (tag) => {
        try{
            let page = 1;
            let newFilteredList = [];

            while(newFilteredList.length < 10){
                let listToFilter;
                if(page > 1){
                    let result = await api.get(`?api_key=${key}&method=user.gettopartists&username=${this.state.username}&limit=10&page=${page}&format=json`);
                    listToFilter = result.data.topartists.artist;
                }else{
                    listToFilter = this.state.list;
                }



                for(let item of listToFilter){
                    if(newFilteredList.length !== 10){
                        let result = await api.get(`?api_key=${key}&method=artist.gettoptags&artist=${encodeURIComponent(item.name)}&format=json`)

                        let topTags = result.data.toptags.tag;
                        console.log(item.name)

                        let limit = topTags.length >= 5 ? 5 : topTags.length;

                        for(var i = 0; i < limit ; i++){
                            if(topTags[i].name === tag){
                                newFilteredList.push(item)
                            }
                        }
                    }
                }
                console.log(newFilteredList.length)
                page++;
            }

            this.setState({
                filteredList: newFilteredList
            })
        }catch(e){
            this.setState({
                filteredList: []
            })
        }
    }
    render(){
        var { error, list, filteredList } = this.state;
        var isFirstScreen = this.state.list === null 
        && this.state.filteredList.length === 0;

        return(
            <div className = {isFirstScreen ? 'main' : 'mainWithList'}>
                <UsernameInput
                    getList = {this.loadData}
                    displayOnCenter = {isFirstScreen}
                />
                {error && <p>Username não encontrado</p>}

                {(list !== null &&
                    <div>
                        <TagInput
                            filter = {this.filter} 
                        />
                        <ResultList
                            list = {(filteredList.length > 0 ? filteredList : list)}
                        />
                    </div>
                )}
            </div>
        ) 
    }
}

ReactDOM.render(<Main />, document.getElementById('root'))
