import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import api, { key } from './services/api';


const ResultList = (props) => {
    return(
        <ul>
            {props.list.map((item, index) => {
                return (
                    <li key = {index.toString()}> 
                        {item.name}
                    </li>
                )
            })}

        </ul>
    )
}

const UsernameInput = (props) => {
    const [value, setValue] = useState('')

    return(
        <div>
            <input 
                placeholder='dale'
                onChange = {e => setValue(e.target.value)}
                value = {value}
            />

            <input
                type = 'submit'
                value = 'Buscar'
                onClick = {() => props.getList(value)}
            />
        </div>
    )
}

const TagInput = (props) => {
    const [value, setValue] = useState('')

    return(
        <div>
            <input 
                placeholder='Filtre por tag...'
                onChange = {e => setValue(e.target.value)}
                value = {value}
            />

            <input
                type = 'submit'
                value = 'Filtrar'
                onClick = {() => props.filter(value)}
            />

        </div>
    )
}

/////////

const Main = () => {
    const [list, setList] = useState(null);
    const [filteredList, setFilteredList] = useState([])
    const [username, setUsername] = useState(null)
    const [error, setError] = useState(false);


    const loadData = async (username) => {
        try{
            let result = await api.get(`?api_key=${key}&method=user.gettopartists&username=${username}&limit=10&format=json`);

            let list = result.data.topartists.artist;
            console.log(result)

            setList(list);
            setUsername(username)
            setError(false);
        }catch(e){
            setList(null);
            setError(true);
        }
    }

    const filter = async (tag) => {
        try{
            let page = 1;
            let newFilteredList = filteredList;

            while(newFilteredList.length < 10){
                let listToFilter;
                if(page > 1){
                    let result = await api.get(`?api_key=${key}&method=user.gettopartists&username=${username}&limit=10&page=${page}&format=json`);
                    listToFilter = result.data.topartists.artist;
                }else{
                    listToFilter = list;
                }

                for(let item of listToFilter){
                    let result = await api.get(`?api_key=${key}&method=artist.gettoptags&artist=${item.name}&format=json`)

                    let topTags = result.data.toptags.tag;

                    for(var i = 0; i < 5 ; i++){
                        if(topTags[i].name === tag){
                            newFilteredList.push(item)
                        }
                    }
                }
                page++;
            }

            setFilteredList(newFilteredList);
            console.log(filteredList)
        }catch(e){
            console.log(e)
            setFilteredList([]);
        }
    }

    return(

        <div>
            {console.log(filteredList)}
            <UsernameInput
                getList = {loadData}
            />
            {error && <p>Username n√£o encontrado</p>}

            {(list !== null &&
                <div>
                    <TagInput
                        filter = {filter} 
                    />
                    <ResultList
                        list = {(filteredList.length > 0 ? filteredList : list)}
                    />
                </div>
            )}
        </div>
    ) 

}

ReactDOM.render(<Main />, document.getElementById('root'))
