import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import api, { key } from './services/api';


const ResultList = (props) => {
    return(
        <ul>
            {props.data.chart.map((item, index) => {
                return (
                    <li key = {index.toString()}> 
                        {item.name}
                    </li>
                )
            })}

        </ul>
    )
}

const UsernameInput = (props) => {
    const [value, setValue] = useState('')

    return(
        <div>
            <input 
                placeholder='dale'
                onChange = {e => setValue(e.target.value)}
                value = {value}
            />

            <input
                type = 'submit'
                value = 'Buscar'
                onClick = {() => props.getList(value)}
            />

            {props.error && <p>Username n√£o encontrado</p>}
        </div>
    )
}

/////////

const Main = () => {
    const [data, setData] = useState(null);
    const [error, setError] = useState(false);


    const loadData = async (username) => {
        try{
            let top = await api.get(`?api_key=${key}&method=user.gettopartists&username=${username}&limit=10&format=json`)
            let tags = await api.get(`?api_key=${key}&method=user.gettoptags&user=${username}&format=json`)
            let data = {
                chart: top.data.topartists.artist,
            }


            console.log(tags)
            setData(data)
            setError(false)
        }catch(e){
            setError(true)
        }
    }

    return(
        <div>
            <UsernameInput
                getList = {loadData}
                error = {error}
            />
            {(data !== null &&
                <ResultList
                    data = {data}
                />)
            }
        </div>
    ) 

}

ReactDOM.render(<Main />, document.getElementById('root'))
